mapper.select:
SELECT member_id, email, password, phone, picture, text member_text, cre_date as "member_cre_date", del_date as "member_del_date", permission_id as "permission", designer, member_name as "member_name", permission.permission_id permission_id, permission.permission_name permission_name FROM member JOIN permission ON member.permission_id = permission.permission_id WHERE
email=#{email}
phone=#{phone}

mapper.insert:
	INSERT INTO member
	(

		email
		,password
		,phone
		,picture
		,text
		,permission_id
		,designer
		,member_name	

	)
	VALUES
	(
		#{email}
		,#{password}
		,#{phone}
		,#{picture}
		,#{text}
		,#{permission.permissionId}
		,#{designer}
		,#{memberName}

	)

        SELECT SEQ_Member_ID.currval AS memberId FROM dual

INSERT INTO 
	 agency
	(	
		agency_id
		,member_id
		,name 
		,biznum
		,address_main
		,address_middle
		,address_small
		,address_sub
		,text
		,trans_type_id
		,confirm
	)
	VALUES
	(	
		SEQ_AGENCY_ID.nextval
		,#{member.memberId}
		,#{name}
		,#{biznum}
		,#{addressMain}
		,#{addressMiddle}
		,#{addressSmall}
